import React from "react";
import { Component } from "react";
import PropTypes from "prop-types";
import Button from "@material-ui/core/Button";
import axios from 'axios'
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import Divider from "@material-ui/core/Divider";
import { FormGroup, Grid, Paper, TextField } from "@material-ui/core";

import ColorButton from "../../utils/OrgBuuton";

import Title from "./Title";
import AddForm from "./AddForm";

export default class ADD extends React.Component {
  constructor(props) {
    console.log("Add constructor");
    super(props);
    this.state = {
      open: false,
      name: "ADD ORDER",
      order_Id: 0,
      order_Date: "",
      customer_Name: "",
      customer_Number: 0,
      order_Amount: 0,
      notes: ""
    };
    this.handleClickOpen = this.handleClickOpen.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleClickOpen = (event) => {
    this.setState({
      open: true
    });

    console.log("inside open : ", this.state.open);
  };

  handleClose = () => {
    console.log("inside close");
    this.setState({
      open: false
    });
    console.log("inside close : ", this.state.open);
  };

  handleChange = (event, field) => {
    this.setState({ [field]: event.target.value });
    console.log("in Set state :", this.state);
  };

  handleSubmit = async (event) =>{
      const send = {
        orderID: this.state.order_Id,
        orderDate: this.state.order_Date,
        customerName: this.state.customer_Name,
        customerID: this.state.customer_Number,
        orderAmt: this.state.order_Amount,
        notes: this.state.notes
      }

      axios.post('http://localhost:8080/1729197/add', send)
      .then((response) => {
        console.log(response);
      }, (error) => {
        console.log(error);
      });
      this.handleClose();
      console.log("add data :" , send)

  };

  render() {
    return (
      <div>
        <ColorButton
          variant="contained"
          color="primary"
          onClick={this.handleClickOpen}
        >
          ADD
        </ColorButton>

        <Dialog open={this.state.open} keepMounted>
          <Paper style={{ padding: "2.5px" }}>
            <Title main={this} />

            <AddForm main={this} />

            <div style={{ display: "flex", justifyContent: "center" }}>
              <ColorButton onClick={this.handleClose}
              // yha par onClick wale function m data backend p verify karo
              onClick={this.handleSubmit}
              size="small">
                ADD
              </ColorButton>
            </div>
          </Paper>
        </Dialog>
      </div>
    );
  }
}
