import React, { Component } from 'react'
import { Button, FormGroup, Grid, TextField } from '@material-ui/core'
import axios from 'axios'

import './login.css'
// import AccountCircle from '@material-ui/icons/AccountCircle';
import  Lvl1 from './Level1'

// import Lvl2 from './Level2_UI/Lvl2'
// import Lvl3 from './Level3_UI/Lvl3'

// import Levl1Dash from '../components/Level1_UI/Lvl1.js'



class logIn extends Component {

    constructor(props) {
        super(props);
        this.state = {
            userName: '',
            passWord: '',
            level: '',
            check: false,
            data: ''
        };

        this.handleUserNameChange = this.handleUserNameChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleUserNameChange = (event) => {
        this.setState(
            {
                userName: event.target.value
            });
    }

    handlePasswordChange = (event) => {
        this.setState({
            passWord: event.target.value
        });
    }



    handleSubmit = async (event) => {
        alert(this.state.userName + "   =>    " + this.state.passWord);
        if (!(this.state.userName === "" || this.state.passWord === "")) {
            await axios.post('http://localhost:8080/1729197/logus?user=' + this.state.userName + '&' + 'password=' + this.state.passWord, {
             })
            // await axios.post('http://localhost:8080/1729197/logus' , {
            //     user : this.state.userName,
            //     password : this.state.passWord
            //  })
                .then((response) => {
                    console.log("in response :");
                    console.log(response.data);
                    console.log("user status : ", response.data);
                    if (response.data != '') {
                        this.setState({
                            userName: '',
                            passWord: '',
                            level: '',
                            check: true,
                            data: response.data
                        })
                        console.log("user data -->:", this.state.data)
                        alert("Welcome" )
                    }


                })
                .catch(function (error) {
                    console.log(error);
                });
        }


        if (this.state.data==='') {
            alert("Invalid Credentials");
        }


        event.preventDefault();
    }



    render() {

        const { userName, passWord, level, data } = this.state

        if (!this.state.check) {
            console.log("in render :", this.state.data)

        return (
            <div className ="asd" >

                        <FormGroup id="sda">
                            <h2> Sign In</h2>
                            <TextField id="input-with-icon-grid" label="Username" 
                            style={{width:200}}
                            onChange={this.handleUserNameChange}
                            />
                            
                            <br/><br/>
                            
                            <TextField  id="input-with-icon-grid" label="Password" 
                            type="password"
                            style={{width:200}}
                            onChange={this.handlePasswordChange}
                            />

                        <br/><br/>
                        <Button variant="contained" color="primary"
                        style={{width:100}}
                        onClick={this.handleSubmit}
                        >Log In</Button>
                        </FormGroup>
                    
                    
                </div>
        )
        }
        else if(this.state.data === "Level 1") {
            return (
                <div>
                    <Lvl1 lvl={this.state.data}/>
                </div>
            )
        }
        else if (this.state.data === "Level 2") {
            return (
                <div>
                    {/* <Lvl2/> */}
                    Lvl2
                </div>
            )
        }
        else if (this.state.data === "Level 3") {
            return (
                <div>
                    {/* <Lvl3/> */}
                    lvl3
                </div>
            )
        }

    }
}

export default logIn
