import React, { Component } from 'react'
import axios from 'axios'

import { CircularLoading } from '../utils/CircularLoading'
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Grid from "@material-ui/core/Grid";



import LvL1 from './LvL1Function/LvL1'

const styles = theme => ({
    table: {
        minWidth: 650,
      }
})



export class Handler extends Component {

    constructor(props) {
        super(props);

        this.state = {
            lvl: props.lvl,
            rows: [],
            columns: [],
            seen: false,
            selectedRowID: -1,
        }
    }

    handleEdit(props){
        console.log("editing...................",props.val)
    }

    renderSwitch(param) {

        switch (param) {
            case 'Level 2':
                return (
                    <div>lvl2</div>
                );
            case 'Level 3':
                return (
                    <div>lvl3</div>
                );
            default:
                return (
                    <LvL1 main={this}/>
                );
        }
    }


    render() {

        const { classes } = this.props
        if (!this.state.seen) {
            return (
                <CircularLoading />
            )
        }
        else {
            return (
                <div>
                    {this.renderSwitch(this.state.lvl)}
                    <TableContainer component={Paper}>
                        <Table 
                        aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Order Date</TableCell>
                                    <TableCell align="center">Approved By</TableCell>
                                    <TableCell align="center">Order ID</TableCell>
                                    <TableCell align="center">Compant Name</TableCell>
                                    <TableCell align="center">Company ID</TableCell>
                                    <TableCell align="center">Order Amount</TableCell>
                                    <TableCell align="center">Approval Status</TableCell>
                                    <TableCell align="center">Notes </TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {this.state.rows.map((row) => (
                                    <TableRow key={row.id}>
                                        <TableCell component="th" scope="row">{row.Order_Date}</TableCell>
                                        <TableCell align="center">{row.Approved_By}</TableCell>
                                        <TableCell align="center">{row.Order_Id}</TableCell>
                                        <TableCell align="center">{row.Company_Name}</TableCell>
                                        <TableCell align="center">{row.company_Id}</TableCell>
                                        <TableCell align="center">{row.Order_amount}</TableCell>
                                        <TableCell align="center">{row.Approval_status}</TableCell>
                                        <TableCell align="center">{row.Notes}</TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>

                        </Table>
                    </TableContainer>

                </div>
            )
        }
    }

    componentDidMount() {
        if (!this.state.seen) {
            axios.get('http://localhost:8080/1729197/paging?pNo=' + 1, {
            })
                .then((response) => {
                    console.log("----------------------------------", response);
                    this.setState({
                        seen: true,
                        rows: response.data.data
                    })
                }
                )
        }
    }
}

export default Handler
